async function ChangeOne() {
    let value1 = input.value;
    const url = `https://vedicscriptures.github.io/chapter/${value1}`;
    const response = await fetch(url);
    const data = await response.json();
    console.log(data);
    if(value1==="1") {
        ChangeOne();
        main2.style.display ="none";
        main9.style.display = "block";
        chapters.innerText = `Chapter No ${value1}`;
        name.innerText = data.translation;
        summeryEn.innerText = data.summary.en;
        summeryHi.innerText = data.summary.hi;
    }
}
let verse = document.querySelector("#verse1");
let verseP = document.querySelector("#verseP")
let verseE = document.querySelector("#verseEn")
let verseH = document.querySelector("#verseHi")
 async function changeVerse1() {
    let value1 = input.value;
    let value2 = input2.value;
    const url1 = `https://vedicscriptures.github.io/slok/${value1}/${value2}/`;
    const response = await fetch(url1);
    const data = await response.json();
    console.log(data);
    if(value2==="1")  {
        verse.innerText = `verse:${value2}`
        verseP.innerText = data.slok;
        verseE.innerText = data.siva.et
        verseH.innerText = data.tej.ht 
    }
}
button2.addEventListener("click",changeVerse1)
